    def ok_event(self, Dialog):

        try:
            self.base_image_path = self.lineEdit.text()
            self.base_xml_path = self.lineEdit_2.text()

            self.updated_image_path = self.lineEdit_3.text()
            self.updated_xml_path = self.lineEdit_4.text()

            # self.work_directory = self.lineEdit_5.text()

            path_list = [self.base_image_path, self.base_xml_path,
                         self.updated_image_path, self.updated_xml_path,
                         Dialog]

            # 触发信号 传送数据给主窗口
            Dialog.path_signal.emit(path_list)
            Dialog.accept()

        except Exception as e:
            print(e)
            print(traceback.print_exc())

    def events_bond(self, Dialog):
        """
        事件绑定类
        :return:
        """

        self.pushButton.clicked.connect(lambda: self.open_image_file_event(Dialog, 0))
        self.pushButton_2.clicked.connect(lambda: self.open_xml_file_event(Dialog, 0))
        self.pushButton_3.clicked.connect(lambda: self.open_image_file_event(Dialog, 1))
        self.pushButton_4.clicked.connect(lambda: self.open_xml_file_event(Dialog, 1))
        # self.pushButton_5.clicked.connect(lambda: self.open_work_directory(Dialog))

    def open_image_file_event(self, Dialog, version):
        """
        加载图片
        :return:
        """

        # file_name = QFileDialog.getOpenFileName(self, "打开文件", ".", "Image Files (*.png)")
        # print(file_name)

        try:

            if version == 0:
                dir = '../'
                file_name, file_type = QFileDialog.getOpenFileName(Dialog, "Open Base Version Image ", dir, "(*.png)")
                # self.base_image_path = file_name
                self.lineEdit.setText(file_name)
            else:
                # 获取上一个文件打开的目录上级
                dir = '../'
                text = self.lineEdit.text()
                if text != '':
                    tmp_list = text.split('/')
                    tmp_list = tmp_list[0:-1]
                    sep = '/'
                    dir = sep.join(tmp_list)

                file_name, file_type = QFileDialog.getOpenFileName(Dialog, "Open Updated Version Image ", dir, "(*.png)")
                # self.updated_image_path = file_name
                self.lineEdit_3.setText(file_name)

        except Exception as e:
            print(e)
            print(traceback.print_exc())

    def open_xml_file_event(self, Dialog, version):
        """
        加载Xml文件
        :param Dialog:
        :param version:
        :return:
        """

        try:

            if version == 0:
                # 获取上一个文件打开的目录上级
                dir = '../'
                text = self.lineEdit.text()
                if text != '':
                    tmp_list = text.split('/')
                    tmp_list = tmp_list[0:-1]
                    sep = '/'
                    dir = sep.join(tmp_list)
                file_name, file_type = QFileDialog.getOpenFileName(Dialog, "Open Base Version Xml File ", dir, "(*.xml)")
                # self.base_xml_path = file_name
                self.lineEdit_2.setText(file_name)
            else:
                # 获取上一个文件打开的目录上级
                dir = '../'
                text = self.lineEdit.text()
                if text != '':
                    tmp_list = text.split('/')
                    tmp_list = tmp_list[0:-1]
                    sep = '/'
                    dir = sep.join(tmp_list)

                file_name, file_type = QFileDialog.getOpenFileName(Dialog, "Open Updated Version Xml File", dir, "(*.xml)")
                # self.updated_xml_path = file_name
                self.lineEdit_4.setText(file_name)

        except Exception as e:
            print(e)
            print(traceback.print_exc())

